TL;DR

foo.go
package foo

type BaseModel struct {
	ID string
}


main.go
package main

type Model struct {
	foo.BaseModel
	Name string
}

func main() {
	m := Model{
		Name: "fjnkt98",
		BaseModel: foo.BaseModel{ // NOT `foo.BaseModel: ...`
			ID: "001",
		},
	}
}


