const x:number[]=[1,2,3,4,5];
let [y,z]=x;  // y=1,z=2
2変数の変換
let a:number=1;
let b:number=2;
[a,b]=[b,a]
console.log([a,b]);//a=2,b=1
const x:number[]=[1,2,3,4,5]
let [a,...b]=x;
console.log(a);//a=1
cosole.log(b);//b=[2,3,4,5]
